    { id: 'reports', label: 'Reports', icon: 'üìà' },
    // { id: 'passbook', label: 'Passbook', icon: 'üìñ' },
  ];

  const agentMenuItems = [
    { id: 'agent', label: 'Agent Dashboard', icon: 'üè†' },
    { id: 'collections', label: 'Collections', icon: 'üí∞' },
    { id: 'customers', label: 'My Customers', icon: 'üë•' },
  ];

  const customerMenuItems = [
    { id: 'overview', label: 'My Account', icon: 'üë§' },
    { id: 'passbook', label: 'My Passbook', icon: 'üìñ' },
    { id: 'collections', label: 'Payment History', icon: 'üí∞' },
  ];

  const getMenuItems = () => {
    switch (userType) {
      case 'agent':
        return agentMenuItems;
      case 'customer':
        return customerMenuItems;
      default:
        return adminMenuItems;
    }
  };

  const menuItems = getMenuItems();

  return (








            B


Bhavani Chits





User Type


             setUserType('admin')}
              className={`px-3 py-1 text-xs rounded-full ${
                userType === 'admin' 
                  ? 'bg-blue-100 text-blue-700' 
                  : 'bg-gray-100 text-gray-600'
              }`}
            >
              Admin

             setUserType('agent')}
              className={`px-3 py-1 text-xs rounded-full ${
                userType === 'agent' 
                  ? 'bg-green-100 text-green-700' 
                  : 'bg-gray-100 text-gray-600'
              }`}
            >
              Agent

             setUserType('customer')}
              className={`px-3 py-1 text-xs rounded-full ${
                userType === 'customer' 
                  ? 'bg-purple-100 text-purple-700' 
                  : 'bg-gray-100 text-gray-600'
              }`}
            >
              Customer





          {menuItems.map((item) => (
             setActiveTab(item.id)}
              className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg text-left transition-colors ${
                activeTab === item.id
                  ? 'bg-blue-50 text-blue-700 border-r-2 border-blue-700'
                  : 'text-gray-600 hover:bg-gray-50'
              }`}
            >
              {item.icon}
              {item.label}

          ))}



  );
};

