import React, { useState } from 'react';

const ChitSchemeManagement = () => {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingScheme, setEditingScheme] = useState(null);
  const [showMembersModal, setShowMembersModal] = useState(false);
  const [selectedScheme, setSelectedScheme] = useState(null);

  // Static chit schemes data
  const [schemes, setSchemes] = useState([
    {
      id: 1,
      name: '‚Çπ5,00,000 - 30 months',
      chitValue: 500000,
      duration: 30,
      durationType: 'months',
      dailyPayment: 500,
      monthlyPayment: 15000,
      numberOfMembers: 30,
      auctionRules: 'Before lifting: ‚Çπ500 daily, After lifting: ‚Çπ500 daily',
      status: 'Active',
      membersEnrolled: 25,
      startDate: '2024-01-01',
      endDate: '2026-07-01'
    },
    {
      id: 2,
      name: '‚Çπ5,00,000 - 200 days',
      chitValue: 500000,
      duration: 200,
      durationType: 'days',
      dailyPayment: 2500,
      monthlyPayment: 75000,
      numberOfMembers: 20,
      auctionRules: 'Before lifting: ‚Çπ2500 daily, After lifting: ‚Çπ3000 daily',
      status: 'Active',
      membersEnrolled: 18,
      startDate: '2024-02-01',
      endDate: '2024-08-19'
    },
    {
      id: 3,
      name: '‚Çπ3,00,000 - 18 months',
      chitValue: 300000,
      duration: 18,
      durationType: 'months',
      dailyPayment: 300,
      monthlyPayment: 9000,
      numberOfMembers: 25,
      auctionRules: 'Before lifting: ‚Çπ300 daily, After lifting: ‚Çπ300 daily',
      status: 'Completed',
      membersEnrolled: 25,
      startDate: '2023-01-01',
      endDate: '2024-07-01'
    }
  ]);

  const [formData, setFormData] = useState({
    name: '',
    chitValue: '',
    duration: '',
    durationType: 'months',
    dailyPayment: '',
    monthlyPayment: '',
    numberOfMembers: '',
    auctionRules: '',
    status: 'Active',
    startDate: new Date().toISOString().split('T')[0]
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingScheme) {
      setSchemes(prev => prev.map(scheme => 
        scheme.id === editingScheme.id ? { ...formData, id: editingScheme.id } : scheme
      ));
      setEditingScheme(null);
    } else {
      const newScheme = {
        ...formData,
        id: schemes.length + 1,
        membersEnrolled: 0,
        startDate: formData.startDate || new Date().toISOString().split('T')[0],
        endDate: calculateEndDate(formData.startDate || new Date().toISOString().split('T')[0], formData.duration, formData.durationType)
      };
      setSchemes(prev => [...prev, newScheme]);
    }
    setShowCreateForm(false);
    setFormData({
      name: '',
      chitValue: '',
      duration: '',
      durationType: 'months',
      dailyPayment: '',
      monthlyPayment: '',
      numberOfMembers: '',
      auctionRules: '',
      status: 'Active',
      startDate: new Date().toISOString().split('T')[0]
    });
  };

  const calculateEndDate = (startDate, duration, durationType) => {
    const start = new Date(startDate);
    if (durationType === 'months') {
      start.setMonth(start.getMonth() + parseInt(duration));
    } else {
      start.setDate(start.getDate() + parseInt(duration));
    }
    return start.toISOString().split('T')[0];
  };

  const handleEdit = (scheme) => {
    setEditingScheme(scheme);
    setFormData(scheme);
    setShowCreateForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this chit scheme?')) {
      setSchemes(prev => prev.filter(scheme => scheme.id !== id));
    }
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'paused': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getProgressPercentage = (enrolled, total) => {
    return Math.round((enrolled / total) * 100);
  };

  return (


      {/* Header */}



Chit Scheme Management
         setShowCreateForm(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
        >
          + Create New Scheme



      {/* Predefined Schemes */}



Predefined Schemes





