                 handleLogin('Client')}
                  className="w-full px-5 py-3 text-sm font-medium text-violet-600 border border-violet-600 rounded-xl hover:bg-violet-600 hover:text-white transition-all duration-200 dark:text-violet-400 dark:border-violet-400 dark:hover:bg-violet-400 dark:hover:text-gray-900 hover:shadow-md"
                >
                  Client Login

                 handleLogin('Admin')}
                  className="w-full px-5 py-3 text-sm font-medium text-white bg-violet-600 rounded-xl hover:bg-violet-700 transition-all duration-200 dark:bg-violet-500 dark:hover:bg-violet-600 hover:shadow-md"
                >
                  Admin Login




        </>
      )}



  );
};

export default Navbar;

import React, { useState } from 'react';

const CollectionManagement = () => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [showAddCollection, setShowAddCollection] = useState(false);
  const [editingCollection, setEditingCollection] = useState(null);

  // Static collection data
  const [collections, setCollections] = useState([
    {
      id: 1,
      customerName: 'Rajesh Kumar',
      customerId: 'C001',
      amountPaid: 500,
      collectorName: 'Amit Singh',
      date: '2024-12-14',
      balanceRemaining: 44500,
      paymentMethod: 'Cash',
      remarks: 'On time payment'
    },
    {
      id: 2,
      customerName: 'Priya Sharma',
      customerId: 'C002',
      amountPaid: 2500,
      collectorName: 'Sunita Patel',
      date: '2024-12-14',
      balanceRemaining: 122500,
      paymentMethod: 'Bank Transfer',
      remarks: 'Early payment'
    },
    {
      id: 3,
      customerName: 'Amit Singh',
      customerId: 'C003',
      amountPaid: 500,
      collectorName: 'Vikram Reddy',
      date: '2024-12-14',
      balanceRemaining: 44500,
      paymentMethod: 'Cash',
      remarks: 'Regular payment'
    },
    {
      id: 4,
      customerName: 'Sunita Patel',
      customerId: 'C004',
      amountPaid: 0,
      collectorName: 'Amit Singh',
      date: '2024-12-14',
      balanceRemaining: 180000,
      paymentMethod: 'Not Paid',
      remarks: 'Defaulted - 3 days overdue'
    }
  ]);

  const [formData, setFormData] = useState({
    customerName: '',
    customerId: '',
    amountPaid: '',
    collectorName: '',
    date: selectedDate,
    balanceRemaining: '',
    paymentMethod: 'Cash',
    remarks: ''
  });

  const collectors = ['Amit Singh', 'Sunita Patel', 'Vikram Reddy', 'Anita Gupta'];
  const paymentMethods = ['Cash', 'Bank Transfer', 'UPI', 'Cheque', 'Not Paid'];

  const filteredCollections = collections.filter(collection => 
    collection.date === selectedDate
  );

  const dailySummary = {
    totalCollected: filteredCollections.reduce((sum, collection) => sum + collection.amountPaid, 0),
    paidMembers: filteredCollections.filter(collection => collection.amountPaid > 0).length,
    pendingMembers: filteredCollections.filter(collection => collection.amountPaid === 0).length,
    totalMembers: filteredCollections.length
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingCollection) {
      setCollections(prev => prev.map(collection => 
        collection.id === editingCollection.id ? { ...formData, id: editingCollection.id } : collection
      ));
      setEditingCollection(null);
    } else {
      const newCollection = {
        ...formData,
        id: collections.length + 1,
        amountPaid: parseInt(formData.amountPaid) || 0
      };
      setCollections(prev => [...prev, newCollection]);
    }
    setShowAddCollection(false);
    setFormData({
      customerName: '',
      customerId: '',
      amountPaid: '',
      collectorName: '',
      date: selectedDate,
      balanceRemaining: '',
      paymentMethod: 'Cash',
      remarks: ''
    });
  };

  const handleEdit = (collection) => {
    setEditingCollection(collection);
    setFormData(collection);
    setShowAddCollection(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this collection record?')) {
      setCollections(prev => prev.filter(collection => collection.id !== id));
    }
  };

  const getPaymentStatusColor = (amount) => {
    if (amount === 0) return 'bg-red-100 text-red-800';
    if (amount > 0) return 'bg-green-100 text-green-800';
    return 'bg-gray-100 text-gray-800';
  };

  const getPaymentStatusText = (amount) => {
    if (amount === 0) return 'Not Paid';
    if (amount > 0) return 'Paid';
    return 'Unknown';
  };

  return (


      {/* Header */}



Collection Management


           setSelectedDate(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
           setShowAddCollection(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
          >
            + Add Collection




      {/* Daily Summary */}









Total Collected

‚Çπ{dailySummary.totalCollected.toLocaleString()}



              üí∞











Paid Members

{dailySummary.paidMembers}



              ‚úÖ











Pending Members

{dailySummary.pendingMembers}



              ‚ö†Ô∏è











Total Members

{dailySummary.totalMembers}



              üë•





      {/* Add/Edit Collection Form Modal */}
      {showAddCollection && (






              {editingCollection ? 'Edit Collection' : 'Add New Collection'}






                  Customer Name




                  Customer ID








                  Amount Paid (‚Çπ)




                  Balance Remaining (‚Çπ)








                  Collector Name

                    Select Collector
                    {collectors.map((collector, index) => (
                      {collector}
                    ))}




                  Payment Method

                    {paymentMethods.map((method, index) => (
                      {method}
                    ))}






                Remarks





                 {
                    setShowAddCollection(false);
                    setEditingCollection(null);
                    setFormData({
                      customerName: '',
                      customerId: '',
                      amountPaid: '',
                      collectorName: '',
                      date: selectedDate,
                      balanceRemaining: '',
                      paymentMethod: 'Cash',
                      remarks: ''
                    });
                  }}
                  className="px-4 py-2 text-gray-600 border border-gray-300 rounded-lg hover:bg-gray-50"
                >
                  Cancel


                  {editingCollection ? 'Update Collection' : 'Add Collection'}





      )}

      {/* Daily Collection Sheet */}






            Daily Collection Sheet - {new Date(selectedDate).toLocaleDateString()}








                Customer
                Amount Paid
                Collector
                Payment Method
                Balance
                Status
                Actions



              {filteredCollections.map((collection) => (






{collection.customerName}

ID: {collection.customerId}



                    ‚Çπ{collection.amountPaid.toLocaleString()}


                    {collection.collectorName}


                    {collection.paymentMethod}


                    ‚Çπ{collection.balanceRemaining.toLocaleString()}



                      {getPaymentStatusText(collection.amountPaid)}





                       handleEdit(collection)}
                        className="text-blue-600 hover:text-blue-900"
                      >
                        Edit

                       handleDelete(collection.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        Delete




              ))}





      {/* Export Options */}



Export Options



            Export to Excel


            Export to PDF


            Print Collection Sheet




  );
};

export default CollectionManagement;
